package com.java.practice;

public class loopsexp2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		int sum=0;
		while(i<=50) {
			if(i%2==0) {
				sum+=i;
				}
			
			i++;
		}
		System.out.println("sum of even numbers:"+sum);
		
		

	}

}
